        bezierPath.addCurve(to: CGPoint(x: play.minX + 34.5, y: play.minY + 63), controlPoint1: CGPoint(x: play.minX + 6, y: play.minY + 50.24), controlPoint2: CGPoint(x: play.minX + 18.76, y: play.minY + 63))
        bezierPath.addCurve(to: CGPoint(x: play.minX + 63, y: play.minY + 34.5), controlPoint1: CGPoint(x: play.minX + 50.24, y: play.minY + 63), controlPoint2: CGPoint(x: play.minX + 63, y: play.minY + 50.24))
        bezierPath.addCurve(to: CGPoint(x: play.minX + 34.5, y: play.minY + 6), controlPoint1: CGPoint(x: play.minX + 63, y: play.minY + 18.76), controlPoint2: CGPoint(x: play.minX + 50.24, y: play.minY + 6))
        bezierPath.close()
        bezierPath.move(to: CGPoint(x: play.minX + 69, y: play.minY + 34.5))
        bezierPath.addCurve(to: CGPoint(x: play.minX + 34.5, y: play.minY + 69), controlPoint1: CGPoint(x: play.minX + 69, y: play.minY + 53.55), controlPoint2: CGPoint(x: play.minX + 53.55, y: play.minY + 69))
        bezierPath.addCurve(to: CGPoint(x: play.minX, y: play.minY + 34.5), controlPoint1: CGPoint(x: play.minX + 15.45, y: play.minY + 69), controlPoint2: CGPoint(x: play.minX, y: play.minY + 53.55))
        bezierPath.addCurve(to: CGPoint(x: play.minX + 13.66, y: play.minY + 7.01), controlPoint1: CGPoint(x: play.minX, y: play.minY + 23.28), controlPoint2: CGPoint(x: play.minX + 5.36, y: play.minY + 13.31))
        bezierPath.addCurve(to: CGPoint(x: play.minX + 34.5, y: play.minY), controlPoint1: CGPoint(x: play.minX + 19.45, y: play.minY + 2.61), controlPoint2: CGPoint(x: play.minX + 26.67, y: play.minY))
        bezierPath.addCurve(to: CGPoint(x: play.minX + 69, y: play.minY + 34.5), controlPoint1: CGPoint(x: play.minX + 53.55, y: play.minY), controlPoint2: CGPoint(x: play.minX + 69, y: play.minY + 15.45))
        bezierPath.close()
        MightyMetUI.transparent.setFill()
        bezierPath.fill()


        if (isNotRunning) {
            //// Bezier 2 Drawing
            let bezier2Path = UIBezierPath()
            bezier2Path.move(to: CGPoint(x: play.minX + 25, y: play.minY + 15))
            bezier2Path.addLine(to: CGPoint(x: play.minX + 53, y: play.minY + 33))
            bezier2Path.addLine(to: CGPoint(x: play.minX + 25, y: play.minY + 54))
            bezier2Path.addLine(to: CGPoint(x: play.minX + 25, y: play.minY + 15))
            bezier2Path.close()
            MightyMetUI.transparent.setFill()
            bezier2Path.fill()
        }


        if (isRunning) {
            //// Pause
            //// Rectangle Drawing
            let rectanglePath = UIBezierPath(rect: CGRect(x: play.minX + 23, y: play.minY + 19, width: 8, height: 31))
            MightyMetUI.yellowOrange.setFill()
            rectanglePath.fill()


            //// Rectangle 2 Drawing
            let rectangle2Path = UIBezierPath(rect: CGRect(x: play.minX + 37, y: play.minY + 19, width: 8, height: 31))
            MightyMetUI.yellowOrange.setFill()
            rectangle2Path.fill()


        }


        context.endTransparencyLayer()
        context.restoreGState()
    }

